sysauto_script.script:
//Get today's time and date
var rightNow = new GlideDateTime();
var daylaterNow = new GlideDateTime();
//Get time and date 24 hours later
var dayLater = new GlideTime();
dayLater.setValue("24:00:00");
daylaterNow.add(dayLater);
//Query the database for NeedIt Task records with Due date field values within 
//24 hours from now. Only return NeedIt Task records that do not have a State 
// field value of Closed Complete.
var dueSoonNITasks = new GlideRecord('x_58872_needit_needit_task');
dueSoonNITasks.addQuery('due_date','>=',rightNow);
dueSoonNITasks.addQuery('due_date','<=',daylaterNow);
//GlideSystem:hoursAgo(...) method looks into the past. When passed a negative number,
//the method looks into the future. 
//dueSoonNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
dueSoonNITasks.addQuery('state','!=',3);
dueSoonNITasks.query();

gs.info("Scheduled Job Find NeedIt Tasks Due Soon executed!");
// Write a log message for each NeedIt Task Record due within 24 hours from now
while(dueSoonNITasks.next()){
	gs.info("NeedIt Task due soon record " + dueSoonNITasks.number);
	gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',dueSoonNITasks,dueSoonNITasks.number, gs.getUserName(),'my_queue');
}
